<template>
  <div class="mb-2">
    <Form layout="inline">
      <Form.Item label="标题">
        <Input v-model:value="search.title" :allowClear="true" @pressEnter="() => tableRef.useReload()"/>
      </Form.Item>
      <Form.Item>
        <Button type="primary" @click="() => tableRef.useReload()">查询</Button>
      </Form.Item>
    </Form>
  </div>
  <ApiBasicTable
   ref="tableRef"
   :api="apiLoadData"
   :columns="columns"
   :action-width="200"
   :search-param="search"
   :show-id="false"
   :show-page="true">
   <template #bodyCell="{column, record }">
     <Button.Group v-if="column.key == '_action'">
       <Button type="link" @click="emit('on-edit', record.id)">编辑</Button>
       <Popconfirm title="是否确定删除?" @confirm="() => { handleDel(record.id) }">
         <Button type="link" danger>删除</Button>
       </Popconfirm>
     </Button.Group>
     <Image v-if="column.dataIndex === 'thumb'" :src="record.thumb" :height="40"
             fallback="data:image/png;base64,"/>
     <EnumLabel v-if="column.dataIndex === 'state'" :dict-value="record.state" dict-code="CmsRecommendConstant$Status"/>
   </template>
 </ApiBasicTable>
</template>

<script lang="ts" setup>
 import { ref } from 'vue';
 import { Button, Popconfirm, Form, Input, Image } from 'ant-design-vue';
 import { loadingTask } from '/@/utils/helper';
 import ApiBasicTable from '/@/components/Kernel/BasicTable/ApiBasicTable.vue';
 import api from '/@/api/index'
import EnumLabel from '/@/components/Kernel/EnumLabel/index.vue'

 const emit = defineEmits(['on-add', 'on-edit'])
 const tableRef = ref<any>(null);

 const prop = defineProps<{recommendId: number}>()

 const search = ref<any>({
  title: '',
  page_no: 1,
  page_size: 30
})
 const apiLoadData = async (e: any): Promise<any> => {
    e.recommend_id = prop.recommendId
    return await api.cms.recommendIndex.curdApi("LIST", e)
 }

 const columns: any[] = [
  { dataIndex: 'thumb', title: '封面图', key: 'title', width: 60 },
   { dataIndex: 'title', title: '标题', key: 'title', width: 200 },
   { dataIndex: 'state', title: '状态', key: 'title', width: 90 },
   { dataIndex: 'sort_num', title: '排序', key: 'title', width: 90 },
 ];

 const handleDel = (e: number) => {
   console.debug(e);
   loadingTask(async () => {
    await api.cms.recommendIndex.curdApi('DELETE', e)
    tableRef.value && tableRef.value.useReload()
   }, {
     msg: '删除中...'
   })
 }

 defineExpose({
   useReload: () => { tableRef.value && tableRef.value.useReload() },
 })

</script>
